document.addEventListener("readystatechange",e=>{if("complete"===e.target.readyState){for(var r=document.getElementsByClassName("bid-ends"),t=new Array,n=0;n<r.length;n++)t[n]=new Array,t[n].el=r[n],t[n].time=new Date(r[n].getAttribute("data-date")).getTime(),t[n].days=0,t[n].hours=0,t[n].seconds=0,t[n].minutes=0;setInterval(function(){for(var e=0;e<t.length;e++){var r=(new Date).getTime(),r=t[e].time-r;t[e].days=Math.floor(r/864e5),t[e].hours=Math.floor(r%864e5/36e5),t[e].minutes=Math.floor(r%36e5/6e4),t[e].seconds=Math.floor(r%6e4/1e3),r<0?(t[e].el.querySelector(".days").innerHTML=0,t[e].el.querySelector(".hours").innerHTML=0,t[e].el.querySelector(".minutes").innerHTML=0,t[e].el.querySelector(".seconds").innerHTML=0):(t[e].el.querySelector(".days").innerHTML=t[e].days,t[e].el.querySelector(".hours").innerHTML=t[e].hours,t[e].el.querySelector(".minutes").innerHTML=t[e].minutes,t[e].el.querySelector(".seconds").innerHTML=t[e].seconds)}},1e3)}});